import React from "react";
import { useState, useEffect } from "react";

import SearchCardItem from "./SearchCardItem";
import SearchCardGroup from "./SearchCardGroup";

const SearchCardItemList = (props) => {

  if (props.filterby == 'groups'){

    const [groups, setGroups] = useState([]);

    useEffect(() => {
        fetch('http://127.0.0.1:8000/search?string=' + props.search + '&orderby=' + props.orderby + '&filterby=' + props.filterby)
          .then((res) => {
            return res.json();
          })
          .then((data) => {
            console.log(data);
            setGroups(data);
          });
    }, [])

    return(
                        <div class="scrollable-card-group my-cards">
                            {groups.map((group) => (
                                <SearchCardGroup id={group._id}/>
                            ))}
                        </div>
    );

  } else {

    const [items, setItems] = useState([]);

    useEffect(() => {
        fetch('http://127.0.0.1:8000/search?string=' + props.search + '&orderby=' + props.orderby + '&filterby=' + props.filterby)
          .then((res) => {
            return res.json();
          })
          .then((data) => {
            console.log(data);
            setItems(data);
          });
    }, [])

    return(
                        <div class="scrollable-card-group my-cards">
                            {items.map((item) => (
                                <SearchCardItem id={item._id}/>
                            ))}
                        </div>
    );

  }
};

export default SearchCardItemList;
