import React from "react";

import ItemIcon0 from "../img/test-item-skyrim.png"
import ItemIcon1 from "../img/test-item-gta-v.png"
import ItemNotFound from "../img/item-not-found.png"

const ItemCard = (props) => {
    var title = "Ítem no encontrado :(";
    var type = "?";
    var genres = "?";
    var icon = ItemNotFound;
    var score = 0;

    // HACER CONSULTA A BD
    if (props.id == "0") {
        title = "The Elder Scrolls V: Skyrim";
        type = "videojuego";
        genres = "aventura, acción";
        icon = ItemIcon0;
        score = 65;
    } else if (props.id == "1") {
        title = "Grand Theft Auto V";
        type = "videojuego";
        genres = "acción, crimen";
        icon = ItemIcon1;
        score = 80;
    }

    //FIJAR EL COLOR DEPENDIENDO DE LA VALORACIÓN
    var score_text = <span class="rounded p-1">{score}/100</span>;
    if (score<50) {
        score_text = <span class="valoracion-negativa rounded p-1">{score}/100</span>;
    } else if (score<70) {
        score_text = <span class="valoracion-mixta rounded p-1">{score}/100</span>;
    } else if (score<90) {
        score_text = <span class="valoracion-positiva rounded p-1">{score}/100</span>;
    } else {
        score_text = <span class="valoracion-muy-positiva rounded p-1">{score}/100</span>;
    }

    return(
                            <div class="scrollable-card item-in-home">
                                <div class="card">
                                    <a class="btn m-0 p-0 text-start" href="/item">
                                        <img src={icon} class="card-img-top" alt="Item icon" height="300" width="200"/>
                                        <div class="card-body">
                                            <h5 class="card-title">{title}</h5>
                                            <p class="card-text scrollable-p">
                                                Tipo: {type}<br/>
                                                Géneros: {genres}
                                            </p>
                                        </div>
                                        <div class="card-footer">
                                            <h5>Puntuación: {score_text}</h5>
                                        </div>
                                    </a>
                                </div>
                            </div>
    );
};

export default ItemCard;
