import React from "react";

import { useState, useEffect } from 'react';
import { useCookies } from 'react-cookie';

const ListMenu = (props) => {

    const [cookies, setCookie, removeCookie] = useCookies(['session']);
    const session = cookies['session'];

    //EXTRAER DE LA BD
    const [date, setDate] = useState({
        date: '',
        year: '',
        month: '',
        day: ''
    });

    useEffect(() => {
        fetch('http://127.0.0.1:8000/list?id=' + props.id)
          .then((res) => {
            return res.json();
          })
          .then((data) => {
            console.log(data);
            setDate({
                date: data.created_at
            });
          });
    }, [])
    /*

    const a = created_at.substring(0,4);
    const mm = created_at.substring(5,7);
    var d = created_at.substring(8,10);

    var m = '';

    switch(mm) {
        case '01':
            m = 'enero';
            break;
        case '02':
            m = 'febrero';
            break;
        case '03':
            m = 'marzo';
            break;
        case '04':
            m = 'abril';
            break;
        case '05':
            m = 'mayo';
            break;
        case '06':
            m = 'junio';
            break;
        case '07':
            m = 'julio';
            break;
        case '08':
            m = 'agosto';
            break;
        case '09':
            m = 'septiembre';
            break;
        case '10':
            m = 'octubre';
            break;
        case '11':
            m = 'noviembre';
            break;
        case '12':
            m = 'diciembre';
            break;
        default:
            m = 'mes indefinido';
            break;
    }

    if (d.substring(0,1) == '0') d = d.substring(1,2)

    */

    if (session != undefined && session.user_id == props.creator_id) {
        return(
            <>
                    <div class="col-6">
                        <p>Lista creada por t√≠ el {props.created_at}</p>
                    </div>
                    <div class="col-6 text-end">
                        <a class="btn bg-dark text-white" href={"/edit-list?id=" + props.id}>Editar lista</a>
                    </div>
            </>
        );
    } else {
        return(
            <>
                    <div class="col-12">
                        <p>Lista creada por <a href={"/user?id=" + props.creator_id}>{props.creator_id /* change to username */}</a> el {} de {} de {}</p>
                    </div>
            </>
        );
    }
}

export default ListMenu;
