import React from "react";

import { useState, useEffect } from "react";

import Comment from "../components/Comment"

const Discussion = () => {
    const queryParameters = new URLSearchParams(window.location.search)
    const id = queryParameters.get("id")

    //EXTRAER DISCUSIÓN DE LA BD

    const [myDiscussion, setMyDiscussion] = useState([]);
    const [myGroup, setMyGroup] = useState([]);
    const [myUser, setMyUser] = useState([]);

    const [date, setDate] = useState({
        year: '',
        month: '',
        day: ''
    });

    useEffect(() => {
        fetch('http://127.0.0.1:8000/discussion?id=' + id)
          .then((res) => {
            return res.json();
          })
          .then((data) => {
            console.log(data);
            setMyDiscussion(data);

            let yyyy = data.created_at.substring(0,4);
            let mm = data.created_at.substring(5,7);
            switch(mm) {
                case '01':
                    mm = 'enero';
                    break;
                case '02':
                    mm = 'febrero';
                    break;
                case '03':
                    mm = 'marzo';
                    break;
                case '04':
                    mm = 'abril';
                    break;
                case '05':
                    mm = 'mayo';
                    break;
                case '06':
                    mm = 'junio';
                    break;
                case '07':
                    mm = 'julio';
                    break;
                case '08':
                    mm = 'agosto';
                    break;
                case '09':
                    mm = 'septiembre';
                    break;
                case '10':
                    mm = 'octubre';
                    break;
                case '11':
                    mm = 'noviembre';
                    break;
                case '12':
                    mm = 'diciembre';
                    break;
                default:
                    mm = 'mes indefinido';
                    break;
            }
            let dd = data.created_at.substring(8,10);
            if (dd.substring(0,1) == '0') dd = dd.substring(1,2);
            setDate({
                year: yyyy,
                month: mm,
                day: dd
            });

            fetch('http://127.0.0.1:8000/group?id=' + data.group_id)
            .then((res) => {
                return res.json();
            })
            .then((data) => {
                console.log(data);
                setMyGroup(data);
            });

            fetch('http://127.0.0.1:8000/user?id=' + data.creator_id)
            .then((res) => {
                return res.json();
            })
            .then((data) => {
                console.log(data);
                setMyUser(data);
            });
          });
    }, [])

    //EXTRAER DISCUSIÓN DE LA BD

    /*
    const [comments, setComments] = useState([]);

    useEffect(() => {
        fetch('http://127.0.0.1:8000/comments?discussion_id=' + id)
            .then((res) => {
                return res.json();
            })
            .then((data) => {
                console.log(data);
                setComments(data);
            });
    })
    */

    return(
        <main class="m-5">
                <div class="row mt-3 mb-4 text-left text-dark">
                    <div class="col-12 ps-5 pe-5">
                        <h3>{myDiscussion.title}</h3>
                    </div>
                </div>

                <div class="row mb-4 text-left text-dark">
                    <div class="col-12 ps-5 pe-5">
                        <p>Discusión de <a href={"/group?id=" + myGroup._id}>{myGroup.name}</a></p>
                    </div>
                </div>

                <div class="row mb-5">
                    <div class="col-11 ps-5 pe-5">
                    <div class="scrollable-card-group my-cards">
                            <div class="scrollable-card my-card w-100 mb-3">
                                <div class="card">
                                    <div class="card-body d-flex flex-row text-start">
                                        <div class="col-lg-1 col-md-2 col-sm-3">
                                            <div class="row align-items-center">
                                                <a class="btn m-0 p-0" href={"/user?id=" + myDiscussion.creator_id}><img class="rounded-circle" src={'/img/' + myUser.avatar} width="80" height="80"/></a>
                                            </div>
                                            
                                        </div>

                                        <div class="col-lg-10 col-md-9 col-sm-8">
                                            <div class="row ms-3">
                                                <a href={"/user?id=" + myDiscussion.creator_id}><h5 class="mr-3">{myUser.name}</h5></a>
                                                <p>{date.year}</p>
                                            </div>

                                            <div class="row ms-3 text-justify">
                                                <p>{myDiscussion.text}</p>
                                            </div>

                                            <div class="row ms-3">
                                                <div class="col-lg-2 col-md-4 col-sm-6">
                                                    <div class="row">
                                                        <p class="pt-1 mr-1">{myDiscussion.likes}</p>
                                                        <a href=""><img src='/img/like.png' width="15" height="15"/></a>
                                                    </div>
                                                </div>

                                                <div class="col-lg-2 col-md-4 col-sm-6">
                                                    <div class="row">
                                                        <p class="pt-1 me-1">{myDiscussion.dislikes}</p>
                                                        <a class="pt-1" href=""><img src='/img/dislike.png' width="15" height="15"/></a>
                                                    </div>
                                                </div>

                                                <div class="col-lg-8 col-md-4 col-sm-0"></div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    <div class="col-1"></div>
                </div>

                <div class="row mt-3">
                    <div class="col-1"></div>

                    <div class="col-11">
                        <h5 class="m-3 ps-4 pe-5">Respuestas</h5>
                    </div>
                </div>

                <div class="row mt-3">
                    <div class="col-1"></div>

                    <div class="col-11 ps-5 pe-5">
                        <textarea name="nuevoComentario" class="form-control w-100" placeholder="Añade un comentario..."></textarea>
                    </div>
                </div>

                <div class="row mt-1 mb-3">
                    <div class="col ps-5 pe-5">
                        <button type="submit" class="rounded p-1 bg-dark text-white float-end">Publicar</button>
                    </div>
                </div>

                <div class="row mb-4 text-left text-dark">
                    <div class="col-1"></div>

