import React from "react";

import { useState, useEffect } from 'react';
import { useCookies } from 'react-cookie';

const GroupMenu = (props) => {

    const [cookies, setCookie, removeCookie] = useCookies(['session']);
    const session = cookies['session'];

    //EXTRAER DE LA BD
    const [myUser, setMyUser] = useState([]);

    const [date, setDate] = useState({
        year: '',
        month: '',
        day: ''
    })

    useEffect(() => {
        fetch('http://127.0.0.1:8000/user?id=' + id)
        .then((res) => {
            return res.json();
        })
        .then((data) => {
            console.log(data);
            setMyUser(data);

            let yyyy = data.created_at.substring(0,4);
            let mm = data.created_at.substring(5,7);
            switch(mm) {
                case '01':
                    mm = 'enero';
                    break;
                case '02':
                    mm = 'febrero';
                    break;
                case '03':
                    mm = 'marzo';
                    break;
                case '04':
                    mm = 'abril';
                    break;
                case '05':
                    mm = 'mayo';
                    break;
                case '06':
                    mm = 'junio';
                    break;
                case '07':
                    mm = 'julio';
                    break;
                case '08':
                    mm = 'agosto';
                    break;
                case '09':
                    mm = 'septiembre';
                    break;
                case '10':
                    mm = 'octubre';
                    break;
                case '11':
                    mm = 'noviembre';
                    break;
                case '12':
                    mm = 'diciembre';
                    break;
                default:
                    mm = 'mes indefinido';
                    break;
            }
            let dd = data.created_at.substring(8,10);
            if (dd.substring(0,1) == '0') dd = dd.substring(1,2);
            setDate({
                year: yyyy,
                month: mm,
                day: dd
            });
        });
    }, [])

    const handleJoin = () => {}

    if (session != undefined && session.user_id == props.creator_id) {
        return(
            <>
                    <div class="col-6 text-start">
                        <p class="mt-3 mb-3 text-dark">Grupo creado por <a href={"/user?id=" + props.creator_id}>t√≠</a> el {props.date.day} de {props.date.month} de {props.date.year}</p>
                    </div>
                    <div class="col-6 text-end">
                        <a class="btn bg-dark text-white" href={"/edit-group?id=" + props.id}>Editar grupo</a>
                    </div>
            </>
        );
    } else {
        return(
            <>
                    <div class="col-6 text-start">
                        <p class="mt-3 mb-3 text-dark">Grupo creado por <a href={"/user?id=" + props.creator_id}>{props.creator_id /* change to username */}</a> el {props.date.day} de {props.date.month} de {props.date.year}</p>
                    </div>
                    <div class="col-6 text-end">
                        <a class="btn bg-dark text-white" onClick={handleJoin}>Unirse a este grupo</a>
                    </div>
            </>
        );
    }
}

export default GroupMenu;
