import React from "react";

import { useState, useEffect } from "react";
import { useCookies } from 'react-cookie';

const User = () => {
    const queryParameters = new URLSearchParams(window.location.search)
    const id = queryParameters.get("id")

    const [cookies, setCookie] = useCookies(['session']);
    const session = cookies['session'];

    //EXTRAER DE LA BD
    const [myUser, setMyUser] = useState([]);

    useEffect(() => {
        fetch('http://127.0.0.1:8000/user?id=' + id)
        .then((res) => {
            return res.json();
        })
        .then((data) => {
            console.log(data);
            setMyUser(data);
        });
    }, [])

    var menu1 = <a class="btn rounded bg-dark text-white float-end" href="/user-edit">Editar perfil</a>
    var menu1 = <a class="btn rounded bg-dark text-white float-end" href="/user-edit">Editar perfil</a>
    
    return(
        <main class="m-5">
                <div class="row mt-3 mb-4 text-center text-dark">
                    <div class="col-12">
                        <h3>Perfil de usuario</h3>
                    </div>
                </div>
                
                <div class="row mb-5">
                    <div class="col-1"></div>

                    <div class="col-lg-3 col-md-4">
                        <img src={'/img/' + myUser.avatar} class="rounded" width="200" height="200"/>
                    </div>

                    <div class="col-lg-7 col-md-6">
                        <h5 class="mt-3">{myUser.name}</h5>
                        <p>{myUser.info}</p>
                    </div>

                    <div class="col-1"></div>
                </div>

                <div class="row mb-5">
                    <div class="col-1"></div>

                    <div class="col-10" id="menu">
                        {menu}
                    </div>
                </div>

                <br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>
            </main>
    );
};

export default User;
