import React from "react";

import ItemIcon from "../img/test-lebowski.jpg";

const SearchCard = (props) => {
    var title = "Ítem no encontrado :(";
    var type = "?";
    var genres = "?";
    var icon = ItemIcon;
    var score = 0;

    //EXTRAER DE LA BD
    if (props.id == "0") {
        title = "The Elder Scrolls V: Skyrim";
        type = "videojuego";
        genres = "aventura, acción";
        icon = ItemIcon0;
        score = 65;
    } else if (props.id == "1") {
        title = "Grand Theft Auto V";
        type = "videojuego";
        genres = "acción, crimen";
        icon = ItemIcon1;
        score = 80;
    }

    var link = "/item?id=" + props.id;

    return(
                            <div class="scrollable-card my-card w-100">
                                <div class="card mb-1">
                                    <div class="card-body d-flex flex-row text-left">
                                        <div class="col-lg-2 col-md-3">
                                            <a class="btn m-0 p-0" href="/item"><img src={ItemIcon} width="120" height="180"/></a>
                                        </div>
                                        <div class="col-lg-10 col-md-9">
                                            <a href="/item">
                                                <h5 class="card-title m-0 p-0">El Gran Lebowski</h5>
                                            </a>
                                            <p class="mt-4">Año: 1998</p>
                                            <p>Dirigida por: Joel Coen, Ethan Coen</p>
                                        </div>
                                    </div>
                                </div>
                            </div>
    );
};

export default SearchCard;
