import React from "react";

import { useState, useEffect } from "react";

import ListMenu from "../components/ListMenu";
import ListContent from "../components/ListContent";

const List = () => {

    const queryParameters = new URLSearchParams(window.location.search)
    const id = queryParameters.get("id")

    //EXTRAER LISTA DE LA BD

    const [myList, setMyList] = useState([]);

    const [myUser, setMyUser] = useState([]);

    useEffect(() => {
        fetch('http://127.0.0.1:8000/list?id=' + id)
          .then((res) => {
            return res.json();
          })
          .then((data) => {
            console.log(data);
            setMyList(data);

            fetch('http://127.0.0.1:8000/user?id=' + data.creator_id)
            .then((res) => {
                return res.json();
            })
            .then((data) => {
                console.log(data);
                setMyUser(data);
            });
          });
    }, ['http://127.0.0.1:8000/list?id=' + id])

    // view

    const handleView = () => {
        alert('Nueva visita')
    }

    return(
        <main class="m-5">
                <div class="row mt-3 mb-4 text-left text-dark">
                    <div class="col-12">
                        <h3>{myList.name}</h3>
                    </div>
                </div>

                <div class="row mb-4 text-left text-dark">
                    <ListMenu id={id} creator_id={myList.creator_id} username={myUser.name} created_at={myList.created_at}/>
                </div>

                <div class="row mb-5">
                    <div class="col-12" onLoad={handleView}>
                        <ListContent id={myList._id} type={myList.type}/>
                    </div>
                </div>
            </main>
    );
};

export default List;
