import React from "react";

import { useState, useEffect } from "react";

import ListMenu from "../components/ListMenu";
import ListContent from "../components/ListContent";

const List = () => {

    const queryParameters = new URLSearchParams(window.location.search)
    const id = queryParameters.get("id")

    //EXTRAER LISTA DE LA BD

    const [myList, setMyList] = useState([]);

    const [myUser, setMyUser] = useState([]);

    useEffect(() => {
        fetch('http://127.0.0.1:8000/list?id=' + id)
          .then((res) => {
            return res.json();
          })
          .then((data) => {
            console.log(data);
            setMyList(data);

            fetch('http://127.0.0.1:8000/user?id=' + data.creator_id)
            .then((res) => {
                return res.json();
            })
            .then((data) => {
                console.log(data);
                setMyUser(data);
            });
          });
    }, ['http://127.0.0.1:8000/list?id=' + id])

    // view

    const [viewed, setViewed] = useState(false);

    const handleView = () => {
        if (!viewed) {
            fetch('http://127.0.0.1:8000/view-list?id=' + id)
            .then((res) => {
                return res.json();
            })
            .then((data) => {
                console.log('Viewing ' + data.modified + ' list(s).');
            })
            .catch((error) => {
                alert(error);
            });

            setViewed(true);
        }
    }

    return(
        <main class="m-5" onLoad={handleView}>
                <div class="row mt-3 mb-4 text-left text-dark">
                    <div class="col-12">
                        <h3>{myList.name}</h3>
                    </div>
                </div>

                <div class="row mb-5 text-left text-dark">
                    <ListMenu id={id} username={myUser.name}/>
                </div>

                <div class="row mb-5">
                    <div class="col-12">
                        <ListContent id={myList._id} type={myList.type} creator_id={myList.creator_id}/>
                    </div>
                </div>
            </main>
    );
};

export default List;
